import { useState, useEffect } from "react";
import Timer from "./components/Timer";
import TaskList from "./components/TaskList";

function App() {
  const [isDark, setIsDark] = useState(true);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", isDark);
  }, [isDark]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white p-6">
      {/* Header */}
      <header className="flex justify-between items-center mb-8">
        <h1 className="text-4xl font-extrabold tracking-tight text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-500">
          ðŸ§  FocusFlow
        </h1>
        <button
          onClick={() => setIsDark(!isDark)}
          className="text-sm bg-gray-700 hover:bg-gray-600 px-3 py-1 rounded"
        >
          {isDark ? "â˜€ Light Mode" : "ðŸŒ™ Dark Mode"}
        </button>
      </header>

      {/* Main Grid */}
      <main className="grid grid-cols-1 lg:grid-cols-2 gap-8 max-w-6xl mx-auto">
        {/* Timer Card */}
        <div className="bg-white/5 backdrop-blur-sm border border-white/10 p-6 rounded-2xl shadow-lg space-y-6">
          <Timer />
        </div>

        {/* Task + Stats Card */}
        <div className="bg-white/5 backdrop-blur-sm border border-white/10 p-6 rounded-2xl shadow-lg space-y-6">
          <TaskList />
        </div>
      </main>

      {/* Footer */}
      <footer className="text-center mt-16 text-sm text-gray-500">
        Built with ðŸ’¡ by <span className="text-blue-400 font-medium">Ragasree Thatipamula</span>
      </footer>
    </div>
  );
}

export default App;
