import { useState, useEffect } from "react";
import Timer from "./components/Timer";
import TaskList from "./components/TaskList";

function App() {
  const [isDark, setIsDark] = useState(true);

  useEffect(() => {
    if (isDark) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [isDark]);

  return (
    <div className="min-h-screen bg-white text-black dark:bg-gray-900 dark:text-white transition-all duration-300 p-6">
      {/* Header */}
      <header className="flex justify-between items-center mb-8">
        <h1 className="text-4xl font-extrabold tracking-tight text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-500">
          ðŸ§  FocusFlow
        </h1>
        <button
          onClick={() => setIsDark(!isDark)}
          className="text-sm bg-gray-200 text-black dark:bg-gray-700 dark:text-white hover:bg-gray-300 dark:hover:bg-gray-600 px-3 py-1 rounded"
        >
          {isDark ? "â˜€ Light Mode" : "ðŸŒ™ Dark Mode"}
        </button>
      </header>

      {/* Main Grid */}
      <main className="grid grid-cols-1 lg:grid-cols-2 gap-8 max-w-6xl mx-auto">
        <div className="bg-white/70 dark:bg-white/5 backdrop-blur-sm border border-white/10 p-6 rounded-2xl shadow-lg space-y-6 transition">
          <Timer />
        </div>
        <div className="bg-white/70 dark:bg-white/5 backdrop-blur-sm border border-white/10 p-6 rounded-2xl shadow-lg space-y-6 transition">
          <TaskList />
        </div>
      </main>

      <footer className="text-center mt-16 text-sm text-gray-600 dark:text-gray-400">
        Built with ðŸ’¡ by <span className="text-blue-500 font-medium">Ragasree Thatipamula</span>
      </footer>
    </div>
  );
}

export default App;
