  return (
    <div className="min-h-screen bg-gray-900 text-white p-4 md:p-10 transition-all duration-300">
      {/* Header */}
      <header className="flex justify-between items-center mb-10">
        <h1 className="text-3xl font-bold tracking-tight">üß† FocusFlow</h1>
        {/* Placeholder for theme toggle or GitHub link */}
        <a
          href="https://github.com/your-username/focusflow"
          target="_blank"
          rel="noreferrer"
          className="text-blue-400 hover:underline"
        >
          GitHub ‚Üó
        </a>
      </header>

      {/* Main Content */}
      <main className="grid grid-cols-1 md:grid-cols-2 gap-10">
        <div className="space-y-6">
          <Timer />
        </div>
        <div className="space-y-6">
          <TaskList />
        </div>
      </main>

      {/* Footer */}
      <footer className="text-center mt-12 text-sm text-gray-500">
        Built with ‚ù§Ô∏è by Ragasree Thatipamula
      </footer>
    </div>
  );
}

export default App;
