import { useState, useEffect } from "react";
import Timer from "./components/Timer";
import TaskList from "./components/TaskList";

function App() {
  const [isDark, setIsDark] = useState(true);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", isDark);
  }, [isDark]);

  return (
    <div className="min-h-screen font-sans bg-white text-gray-800 dark:bg-gray-900 dark:text-white transition-all duration-300 px-4 sm:px-6 lg:px-16 py-8">
      {/* Header */}
      <header className="flex justify-between items-center mb-10">
        <div className="flex items-center space-x-3">
          <span className="text-4xl">ðŸ’¬</span>
          <h1 className="text-3xl sm:text-4xl font-extrabold bg-gradient-to-r from-indigo-400 to-purple-600 text-transparent bg-clip-text tracking-tight">
            FocusFlow
          </h1>
        </div>
        <button
          onClick={() => setIsDark(!isDark)}
          className="bg-gray-200 dark:bg-gray-700 text-sm px-4 py-2 rounded-xl font-medium hover:shadow transition duration-200"
        >
          {isDark ? "â˜€ Light Mode" : "ðŸŒ™ Dark Mode"}
        </button>
      </header>

      {/* Grid Layout */}
      <main className="grid gap-8 grid-cols-1 lg:grid-cols-2">
        {/* Timer */}
        <section className="rounded-3xl p-6 shadow-xl border border-gray-200 dark:border-gray-700 bg-gradient-to-br from-indigo-500 to-purple-600 text-white dark:from-white/10 dark:to-white/5 transition-all">
          <Timer />
        </section>

        {/* Tasks */}
        <section className="rounded-3xl p-6 shadow-xl border border-gray-200 dark:border-gray-700 bg-gradient-to-br from-green-400 to-lime-500 text-gray-900 dark:from-white/10 dark:to-white/5 dark:text-white transition-all">
          <TaskList />
        </section>
      </main>

      {/* Footer */}
      <footer className="mt-16 text-center text-sm text-gray-500 dark:text-gray-400">
        Built with ðŸ’¡ by <span className="text-indigo-400 font-semibold">Ragasree Thatipamula</span>
      </footer>
    </div>
  );
}

export default App;
